/** @test {AtomicBlob} */
describe('AtomicBlob', () => {
  const bitArray = {
    0: 0, 1: 0, 2: 0, 3: 5, 4: 54, 5: 42, 6: 11, 7: 33, 8: 66, 9: 0, 10: 0, 11: 0,
  }

  let atomic

  before(() => {
