chai.use(spies)

/** @test {EventHandler} */
describe('EventHandler', () => {
  let handler

  before(() => {
    handler = new EventHandler()
  })

  /** @test {EventHandler#on} */
  describe('on', () => {
    let spy
    let id

    before(() => {
      spy = chai.spy()
      id = handler.on('/test/path', spy)
    })

    it('returns different subscription ids for each listener', () => {
      const anotherId = handler.on(['test', 'path'], () => {})
      expect(id !== anotherId).to.be.true
    })

    it('registers a handler which can be called', () => {
      handler.notify('/test/path', {})
      expect(spy).to.have.been.called()
    })
  })

  /** @test {EventHandler#off} */
  describe('off', () => {
    let spy
    let id

    before(() => {
      spy = chai.spy()
      id = handler.on('/test/path', spy)
    })

    it('removes a handler', () => {
      const success = handler.off('/test/path', id)
      handler.notify('/test/path', {})

      expect(spy).to.not.have.been.called()
      expect(success).to.be.true
    })

    it('returns false when handler was not found', () => {
      const success = handler.off('/test/path/which/does/not/exist', id)
      expect(success).to.be.false
    })
  })

  /** @test {EventHandler#notify} */
  describe('notify', () => {
    const testdata = {
      test: 'data',
    }

    const spy = []

    before(() => {
      for (let i = 0; i < 12; i += 1) {
        spy.push(chai.spy())
      }

      // regular address handlers
      handler.on('/', spy[0])
      handler.on('/one/test', spy[1])
      handler.on('/and/another', spy[2])
      handler.on('/two/test/path', spy[3])
      handler.on(['two', 'test', 'path'], spy[4])
      handler.on('/two/some/path', spy[5])

      // system event handlers
      handler.on('error', spy[6])
      handler.on('close', spy[7])
      handler.on('open', spy[8])

      // pattern address handlers
      handler.on('/two/*/path', spy[9])
      handler.on('*', spy[10])
      handler.on('/o?e/{test,bar}', spy[11])
    })

    afterEach(() => {
      spy.forEach((item) => {
        item.reset()
      })
    })

    it('passes over the event data', () => {
      handler.notify('/and/another', testdata)
      expect(spy[2]).have.been.called.with(testdata)
    })

    it('accepts messages', () => {
      handler.notify(new Message(['and', 'another']))
      expect(spy[2]).have.been.called()
    })

    it('accepts binary packets', () => {
      const binary = new Uint8Array([
        47, 97, 110, 100, 47, 97, 110,
        111, 116, 104, 101, 114, 0, 0, 0, 0, 44, 0, 0, 0,
      ])

      handler.notify(binary)
      expect(spy[2]).have.been.called()
    })

    describe('event listeners', () => {
      it('notifies error callbacks', () => {
        handler.notify('error', testdata)
        expect(spy[6]).have.been.called.with(testdata)
      })

      it('notifies close callbacks', () => {
        handler.notify('close', testdata)
        expect(spy[7]).have.been.called.with(testdata)
      })

      it('notifies open callbacks', () => {
        handler.notify('open', testdata)
        expect(spy[8]).have.been.called.with(testdata)
      })
    })

    describe('address listeners with timetags', () => {
      it('calls the handler later', () => {
        handler.notify('/', testdata, Date.now() + 5000)

        expect(spy[0]).not.have.been.called()
      })
    })

    describe('address listeners with regular strings', () => {
      it('calls the root listener', () => {
        handler.notify('/', testdata)

        expect(spy[0]).have.been.called()
        expect(spy[1]).not.have.been.called()
        expect(spy[4]).not.have.been.called()
      })

      it('calls two listeners with the same address', () => {
        handler.notify('/two/test/path', testdata)

        expect(spy[3]).have.been.called()
        expect(spy[4]).have.been.called()
      })

      it('works with {} wildcard', () => {
        handler.notify('/two/{test,some}/path', testdata)

        expect(spy[1]).not.have.been.called()
        expect(spy[3]).have.been.called()
        expect(spy[4]).have.been.called()
        expect(spy[5]).have.been.called()
      })

      it('works with [] wildcard', () => {
        handler.notify('/[pawgfo]ne/[bnit]est', testdata)

        expect(spy[1]).have.been.called()
        expect(spy[2]).not.have.been.called()
      })

      it('works with [!] wildcard', () => {
        handler.notify('/two/[!s][eso][tspm][tea]/path', testdata)

        expect(spy[3]).have.been.called()
        expect(spy[5]).not.have.been.called()
      })

      it('works with [a-z] wildcard', () => {
        handler.notify('/two/[a-z]est/p[a-c]t[e-i]', testdata)

        expect(spy[3]).have.been.called()
        expect(spy[5]).not.have.been.called()
      })

      it('works with * wildcard', () => {
        handler.notify('/two/*', testdata)

        expect(spy[3]).have.been.called()
        expect(spy[4]).have.been.called()
        expect(spy[5]).have.been.called()
        expect(spy[1]).not.have.been.called()
      })

      it('works with * wildcard calling all', () => {
        handler.notify('/*', testdata)

        expect(spy[0]).have.been.called()
        expect(spy[1]).have.been.called()
        expect(spy[2]).have.been.called()
        expect(spy[3]).have.been.called()
        expect(spy[4]).have.been.called()
        expect(spy[5]).have.been.called()
      })

      it('works with ? wildcard', () => {
        handler.notify('/two/????/pa?h', testdata)

        expect(spy[0]).not.have.been.called()
        expect(spy[3]).have.been.called()
        expect(spy[5]).have.been.called()
      })
    })

    describe('address listeners with pattern matching', () => {
      it('calls wildcard listeners', () => {
        handler.notify('/two/bar/path', testdata)

        expect(spy[9]).have.been.called()
        expect(spy[10]).have.been.called()
        expect(spy[4]).not.have.been.called()
      })

      it('calls group matching listener', () => {
        handler.notify('/ose/test', testdata)

        expect(spy[10]).have.been.called()
        expect(spy[11]).have.been.called()
        expect(spy[1]).not.have.been.called()
        expect(spy[9]).not.have.been.called()
      })
    })
  })
})

import { expect } from 'chai'

import Message from '../src/message'

/** @test {Message} */
describe('Message', () => {
  let message

  before(() => {
    message = new Message()
  })

  it('contains a set of osc message data', () => {
    expect(message.address).to.exist
    expect(message.types).to.exist
    expect(message.args).to.exist
  })

  it('fills the arguments and address during its construction', () => {
    const anotherMessage = new Message('somekind/of/path', 221.21, 317, 'test')

    expect(anotherMessage.address).to.be.equals('/somekind/of/path')
    expect(anotherMessage.args[0]).to.be.equals(221.21)
    expect(anotherMessage.types).to.be.equals('fis')
  })

  /** @test {Message#add} */
  describe('add', () => {
    before(() => {
      message = new Message()

      message.add('Hello World')
      message.add(121123)
    })

    it('pushes the values to our args array', () => {
      expect(message.args).to.deep.equal(['Hello World', 121123])
    })

    it('changes the types string accordingly', () => {
      expect(message.types).to.equal('si')
    })
  })

  /** @test {Message#pack} */
  describe('pack', () => {
    let result

    before(() => {
      message = new Message()

      message.address = '/sssss/osc/sssssadss'
      message.add(12)
      message.add('Hello World')
      message.add(22111.344)
      message.add(new Uint8Array([100, 52]))

      result = message.pack()
    })

    it('returns an object we can unpack again', () => {
      const anotherMessage = new Message()
      anotherMessage.unpack(new DataView(result.buffer), 0)

      expect(anotherMessage.address).to.equal('/sssss/osc/sssssadss')
      expect(anotherMessage.args[3][0]).to.equal(100)
    })

    it('returns a multiple of 32', () => {
      expect((result.byteLength * 8) % 32).to.equal(0)
    })
  })

  /** @test {Message#unpack} */
  describe('unpack', () => {
    let result
    let anotherMessage

    before(() => {
      anotherMessage = new Message()
      const data = new Uint8Array([
        47, 116, 101, 115, 116, 47, 112, 97,
        116, 104, 0, 0, 44, 105, 0, 0, 0, 0, 2, 141])
      const dataView = new DataView(data.buffer, 0)

      result = anotherMessage.unpack(dataView)
    })

    it('decodes the message correctly', () => {
      expect(anotherMessage.address).to.equal('/test/path')
      expect(anotherMessage.args[0]).to.equal(653)
    })

    it('returns a number', () => {
      expect(result).to.be.a('number')
    })
  })
})

import chai, { expect } from 'chai'
import spies from 'chai-spies-next'

import OSC from '../src/osc'

import Packet from '../src/packet'
import Message from '../src/message'

chai.use(spies)

class TestPlugin {
  constructor() {
    this.socketStatus = -1
    this.notify = null
  }

  registerNotify(fn) {
    this.notify = fn
  }

  status() {
    return this.socketStatus
  }

  open() {
    this.socketStatus = 1
    this.notify('open')
  }

  send() {
    // unused
  }

  close() {
    this.socketStatus = 3
    this.notify('close')
  }

  // mocking helpers
  mockError() {
    this.notify('error', { message: 'An error' })
  }

  mockMessage() {
    this.notify(new Message(['test/path'], 55.1, 224))
  }
}

/** @test {OSC} */
describe('OSC', () => {
  let osc
  let plugin

  before(() => {
    plugin = new TestPlugin()
    osc = new OSC({
      discardLateMessages: true,
      plugin,
    })
  })

  it('returns the instance options when created', () => {
    expect(osc.options.discardLateMessages).to.be.true
  })

  /** @test {OSC#on} */
  describe('on', () => {
    it('calls my subscription when listening to the right address', () => {
      const spy = chai.spy()
      osc.on('/test/path', spy)

      plugin.mockMessage()

      expect(spy).to.have.been.called()
    })

    it('calls an error', () => {
      const spy = chai.spy()
      osc.on('error', spy)

      plugin.mockError()

      expect(spy).to.have.been.called()
    })
  })

  /** @test {OSC#off} */
  describe('off', () => {
    it('removes a subscription', () => {
      const spy = chai.spy()
      const id = osc.on('error', spy)

      osc.off('error', id)

      plugin.mockError()

      expect(spy).to.not.have.been.called()
    })
  })

  /** @test {OSC#status} */
  describe('status', () => {
    it('returns the initial status', () => {
      expect(osc.status()).to.be.equals(OSC.STATUS.IS_NOT_INITIALIZED)
    })
  })

  /** @test {OSC#open} */
  describe('open', () => {
    let spy

    beforeEach(() => {
      spy = chai.spy()
      osc.on('open', spy)
      osc.open()
    })

    it('returns the correct status', () => {
      expect(osc.status()).to.be.equals(OSC.STATUS.IS_OPEN)
    })

    it('calls the open event', () => {
      expect(spy).to.have.been.called()
    })
  })

  /** @test {OSC#close} */
  describe('close', () => {
    let spy

    beforeEach(() => {
      spy = chai.spy()
      osc.on('close', spy)
      osc.close()
    })

    it('returns the correct status', () => {
      expect(osc.status()).to.be.equals(OSC.STATUS.IS_CLOSED)
    })

    it('calls the close event', () => {
      expect(spy).to.have.been.called()
    })
  })

  /** @test {OSC#send} */
  describe('send', () => {
    it('passes over a binary object with configs to the plugin', () => {
      const message = new Message('/test/path', 122, 554)
      const packet = new Packet(message)
      const config = { host: 'localhost', port: 9001 }
      const spy = chai.spy.on(plugin, 'send')
      const binary = packet.pack()

      osc.send(packet, config)

      expect(binary).to.be.a('Uint8Array')
      expect(spy).to.have.been.called.with(binary, config)
    })
  })
})

